import React from 'react';
import './style.css';

class Nama extends React.Component {
  render() {
    return <td>{this.props.nama}</td>;
  }
};

class Harga extends React.Component {
  render() {
    return <td>{this.props.harga}</td>;
  }
}

class Berat extends React.Component {
  render() {
    return <td>{this.props.berat}</td>;
  }
}

let dataHargaBuah = [
  {nama: "Semangka", harga: 10000, berat: 1000},
  {nama: "Anggur", harga: 40000, berat: 500},
  {nama: "Strawberry", harga: 30000, berat: 400},
  {nama: "Jeruk", harga: 30000, berat: 1000},
  {nama: "Mangga", harga: 30000, berat: 500}
]

class BuahInfo extends React.Component {
  render() {
    return (
      <div>
        {dataHargaBuah.map(el=> {
          return (
            
            <table>
            <tr>
            <th style = " width: 40%;" >Nama</th>
            <th>Harga</th>
            <th>Berat</th>
            </tr>

            <tr>
            <Nama nama={el.nama}/> 
            </tr>

            <tr>
            <Harga harga={el.harga}/> 
            </tr>

            <tr>
            <Berat berat={el.berat}/> 
            </tr>
            </table>
            
          )
        })}
      </div>
    )
  }
}


export default BuahInfo;
