      .then(res => {
        let data = res.data
        setDaftarBuah(data.map(el=> {return {id: el.id, name: el.name, price: el.price, weight: el.weight}}))
      })
  const [movie, setMovie] = useState([
    { title: "Harry Potter", duration: 120},
    { title: "Sherlock Holmes", duration: 125},
    { title: "Avengers", duration: 130},
    { title: "Spiderman", duration: 124},
  ]);

  return (
    <MovieContext.Provider value={[movie, setMovie]}>
      {props.children}
    </MovieContext.Provider>
  );
};
