import React, {useContext, useState} from "react"
import { MovieContext } from "./MovieContext"

const MovieForm = ()=>{
  const [movie, setMovie] = useContext(MovieContext)
  const [input, setInput] = useState({id: "", name: "", price:"", weight: ""})
    const[currentId, setCurrentId]= useState(null);

  const handleChange = (event)=>{
    let typeOfInput = event.target.name
    let value = event.target.value
    
    if (typeOfInput === "name"){
      setInput({...input, name: value})
    }else if (typeOfInput === "price"){
      setInput({...input, price: value})
    }else if (typeOfInput === "weight"){
        setInput({...input, weight: value})
    }
  }

  const handleSubmit = (event)=>{
    event.preventDefault()
    if (currentId === null){
        // untuk create data baru
        axios.post(`http://backendexample.sanbercloud.com/api/fruits`, {name: input.name, price: input.price, weight: input.weight })
        .then(res => {
            let data = res.data
            setMovie([...movie, {id: data.id, name: data.name, price: data.price, weight: data.weight}])
        })
      }else{
        axios.put(`http://backendexample.sanbercloud.com/api/fruits/${currentId}`, {name: input.name, price: input.price, weight: input.weight})
        .then(() => {
            let singlePeserta = daftarBuah.find(el=> el.id === currentId)
            singlePeserta.name= inputName
            singlePeserta.price= inputPrice
            singlePeserta.weight = inputWeight
            setMovie([...Movie])
        })      
      }
      setInput({id: "", name: "", price:"", weight: ""})
  }

  
  return(
    <>
      <form onSubmit={handleSubmit}>
        <strong className="label-movie-form">Title</strong>
        <input type="text" value={input.name} onChange={handleChange} name="title"/>
        <br/>
        <strong className="label-movie-form">Duration</strong>
        <input type="number" value={input.price} onChange={handleChange} name="duration"/>
        <br/>
        <button>Submit</button>
      </form>
    </>
  )
}

export default MovieForm
