import React, {useState, useEffect, useContext} from "react"
import axios from "axios"
import { BuahContext } from "./BuahContext"


const BuahForm = () =>{
  // context
  const {daftarBuahState, currentIdState} = useContext(BuahContext)

  const [daftarBuah, setDaftarBuah] = daftarBuahState
  const [currentId, setCurrentId] = currentIdState
  // state
  const [inputName, setInputName] =  useState("")
  const [inputPrice, setInputPrice] =  useState("")
  const [inputWeight, setInputWeight] =  useState("")

  useEffect( () => {
    //edit work with this
    if (currentId !== null){
      axios.get(`http://backendexample.sanbercloud.com/api/fruits/${currentId}`)
      .then(res => {
        let data = res.data
        setInputName(data.name)
        setInputPrice(data.price)
        setInputWeight(data.weight)
      })
    }
  }, [currentId])

  const handleSubmit = (event) =>{
    event.preventDefault()

    if (currentId === null){
      // untuk create data baru
      axios.post(`http://backendexample.sanbercloud.com/api/fruits`, {name: inputName, price: inputPrice, weight: inputWeight })
      .then(res => {
          let data = res.data
          setDaftarBuah([...daftarBuah, {id: data.id, name: data.name, price: inputPrice, weight: inputWeight}])
      })
    }else{
      axios.put(`http://backendexample.sanbercloud.com/api/fruits/${currentId}`, {name: inputName, price: inputPrice, weight: inputWeight})
      .then(() => {
          setDaftarBuah(null)
      })      
    }
    setInputName("")
    setInputPrice("")
    setInputWeight("")
    setCurrentId(null)
  }
const handleChangeName =(event)=>{
    let inputValue = event.target.value
    setInputName(inputValue)
  }
  const handleChangePrice=(event)=>{
    let inputValue = event.target.value
    setInputPrice(inputValue)
  }
  const handleChangeWeight=(event)=>{
    let inputValue = event.target.value
    setInputWeight(inputValue)
  }

  
  return(
    <>
      {/* Form */}
      <h1>Form Buah</h1>
      <form style={{paddingBottom: "20px"}} onSubmit={handleSubmit} className = "formBuah">
          <div >
          <label style={{paddingRight: "470px"}}>
            <strong>Nama:</strong>
          </label>          
          <input required onChange={handleChangeName} value={inputName} type="text" />
          </div>
          <br/>
          <div><label style={{paddingRight: "470px"}}>
            <strong>Harga:</strong>
          </label>          
          <input required onChange={handleChangePrice} value={inputPrice} type="text" /></div>
          <br/>
          <div><label style={{paddingRight: "370px"}}>
            <strong>Berat (dalam gram):</strong>
          </label>          
          <input required onChange={handleChangeWeight} value={inputWeight} type="text" /></div>
          <br/>
          <div style={{textAlign: "right"}}><input type="submit" value="Submit" /></div>
            
          </form>
    </>
  )

  return <></>
}

export default BuahForm
