import React, {Component} from "react"
import './tugas12.css';
class Buah extends Component{

  constructor(props){
    super(props)
    this.state ={
        daftarBuah : [
            {nama: "Semangka", harga: 10000, berat: 1000},
            {nama: "Anggur", harga: 40000, berat: 500},
            {nama: "Strawberry", harga: 30000, berat: 400},
            {nama: "Jeruk", harga: 30000, berat: 1000},
            {nama: "Mangga", harga: 30000, berat: 500}
          ],
      inputNama: "",
      inputHarga: "",
      inputBerat: "",
      currentIndex: -1 // ini berarti create
    }
    this.handleSubmit = this.handleSubmit.bind(this)
    this.handleChangeNama = this.handleChangeNama.bind(this)
    this.handleChangeHarga = this.handleChangeHarga.bind(this)
    this.handleChangeBerat= this.handleChangeBerat.bind(this)
  }

  // method dengan function biasa
  handleSubmit(event){
    event.preventDefault()
    let currentIndex = this.state.currentIndex
    let daftarBuah = this.state.daftarBuah
    let inputNama = this.state.inputNama
    let inputHarga = this.state.inputHarga
    let inputBerat = this.state.inputBerat
    let newObj = {nama: inputNama, harga: inputHarga, berat: inputBerat}
    
    if (currentIndex === -1 ){
      // untuk create
      this.setState({
        daftarBuah: [...daftarBuah, newObj],
        inputNama: "",
        inputHarga: "",
        inputBerat: "",
      })
    }else{
      //untuk edit karena currentIndex bukan -1
      daftarBuah[currentIndex] = newObj;
      this.setState({
        daftarBuah,
        inputNama: "",
        inputHarga: "",
        inputBerat: "",
        currentIndex: -1
      })
    }
  }

  handleChangeNama(event){
    let inputValue = event.target.value
    this.setState({
      inputNama: inputValue
    })
  }
  handleChangeHarga(event){
    let inputValue = event.target.value
    this.setState({
      inputHarga: inputValue
    })
  }
  handleChangeBerat(event){
    let inputValue = event.target.value
    this.setState({
      inputBerat: inputValue
    })
  }
  
  // method dengan arrow function
  handleEdit = (event) =>{
    let index = event.target.value
    let namaBuah = this.state.daftarBuah[index].nama
    let hargaBuah = this.state.daftarBuah[index].harga
    let beratBuah = this.state.daftarBuah[index].berat
    
    this.setState({
      inputNama: namaBuah,
      inputHarga: hargaBuah,
      inputBerat: beratBuah,
      currentIndex: index
    })
  }

  handleDelete = (event) =>{
    let index = parseInt(event.target.value)
    let newdaftarBuah = this.state.daftarBuah.filter((val, idx) => {
      return idx !== index 
    })
    if (this.state.currentIndex === index){
      this.setState({daftarBuah: newdaftarBuah, currentIndex: -1})
    }else{
      this.setState({daftarBuah: newdaftarBuah})
    }
  }

  render(){
    return(
      <div style={{width: "70%", margin: "0 auto"}}>
        <h1 style={{textAlign: "center"}}>Daftar Harga Buah</h1>
        <table className="daftarBuah">
          <thead>
            <tr>
              <th>No</th>
              <th>Nama</th>
              <th>Harga</th>
              <th>Berat</th>
              
              
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
              {
                this.state.daftarBuah.map((val, index)=>{
                  return(                    
                    <tr>
                      <td>{index+1}</td>
                      <td>{val.nama}</td>
                      <td>{val.harga}</td>
                      <td>{val.berat}</td>
                      
                      
                      <td>
                        <button onClick={this.handleEdit} value={index} style={{marginRight: "10px"}}>Edit</button>
                        <button onClick={this.handleDelete} value={index}>Delete</button>
                      </td>
                    </tr>
                  )
                })
              }
          </tbody>
        </table>
        
        <form onSubmit={this.handleSubmit} className = "formBuah">
          <div >
          <label style={{paddingRight: "485px"}}>
            <strong>Nama:</strong>
          </label>          
          <input required onChange={this.handleChangeNama} value={this.state.inputNama} type="text" />
          </div>
          <br/>
          <div><label style={{paddingRight: "485px"}}>
            <strong>Harga:</strong>
          </label>          
          <input required onChange={this.handleChangeHarga} value={this.state.inputHarga} type="text" /></div>
          <br/>
          <div><label style={{paddingRight: "250px"}}>
            <strong>Berat (dalam gram):</strong>
          </label>          
          <input required onChange={this.handleChangeBerat} value={this.state.inputBerat} type="text" /></div>
          <br/>
          <div style={{textAlign: "right"}}><input type="submit" value="Submit" /></div>
          
        </form>
      </div>
    )
  }
}

export default Buah
